# -*- coding: utf-8 -*-
require 'isono'

module Dcmgr
  module NodeModules
    class MetricCollector < Isono::NodeModules::Base
      include Dcmgr::Logger

      initialize_hook do
        app = Isono::Rack::ObjectMethod.new(myinstance)
        job = Isono::NodeModules::JobChannel.new(node)
        job.register_endpoint('metric-collector', Isono::Rack.build do
            use Isono::Rack::DataStore
            run proc { |req, res|
              Thread.current[Models::BaseNew::LOCK_TABLES_KEY] = {}
              app.call(req, res)
            }
          end)
      end

      terminate_hook do
      end

      def update_metric(metric_id, data)
        logger.info("#{metric_id}: #{data}")
      end
    end
  end
end
