# -*- coding: utf-8 -*-
require 'isono'

module Dcmgr
  module NodeModules
    class StaCollector < Isono::NodeModules::Base
      
      initialize_hook do
        app = myinstance
        rpc = Isono::NodeModules::RpcChannel.new(node)
        rpc.register_endpoint('sta-collector', Isono::Rack.build do
                                use Isono::Rack::DataStore
                                run Isono::Rack::ObjectMethod.new(app)
                              end)
      end

      terminate_hook do
      end

      def get_volume(volume_id)
        v = Dcmgr::Models::Volume[volume_id]
        v.merge_pool_data
      end

      def get_snapshot(snapshot_id)
        vs = Dcmgr::Models::VolumeSnapshot[snapshot_id]
        vs.to_hash_document
      end

      def update(data)
        v = Dcmgr::Models::Volume[data[:volume_id]]
        
        if !data[:export_path].nil?
          v.set_fields(data, [:state, :export_path]).save
        elsif !data[:transport_information].nil?
          v.set_fields(data, [:state, :transport_information]).save
        else
          v.set_fields(data, [:state]).save
        end
      end

      def update_snapshot(data)
        vs = Dcmgr::Models::VolumeSnapshot[data[:snapshot_id]]
        vs.set_fields(data, [:state]).save
      end
    end
  end
end
